mapper.update:
update pr set 
active = 'Y'
where prno = #{prno}

update pr set 
active = 'N'
where prno = #{prno}

update pr set 
status = 'Y'
where prno = #{prno}

update pr set 
status = 'N'
where prno = #{prno}

update appy set 
active = 'Y'
where prno = #{prno}

update appy set 
active = 'N'
where appyno = #{appyno}

update appy set 
status = 'Y'
where appyno = #{appyno}

update appy set 
status = 'N'
where appyno = #{appyno}

update pr set 
active = 'N'
where eno = #{eventNo} and prno = #{prno}

update appy set 
active = 'N'
where eno = #{eventNo} and appyno = #{appyno}

update mtc set 
score = #{score}, rev = #{rev}
where mtcno = #{mtcno}
